import cl from './table.module.css';
import React, { useState, useEffect } from 'react';

function CustomerTable1({ FetchUrl }) {
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    fetch(FetchUrl)
      .then(response => response.json())
      .then(data => setCustomers(data.table))
      .catch(error => console.error('Error fetching customers:', error));
  }, [FetchUrl]);

  const handleDelete = (id) => {
    fetch(`http://localhost:8000/delete_customer/${id}`, { method: 'DELETE' })
      .then(response => {
        if (response.ok) {
          setCustomers(customers.filter(customer => customer.id !== id));
        } else {
          console.error('Error deleting customer');
        }
      })
      .catch(error => console.error('Error deleting customer:', error));
  };

  const handleEdit = (id) => {
    // Implement your edit logic here
    alert(`Edit customer with id: ${id}`);
  };

  return (
    <div>
      <table className={cl.container}>
        <thead>
          <tr>
            {customers.length > 0 && Object.keys(customers[0]).map(key => (
              <th key={key}>{key}</th>
            ))}
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((customer) => (
            <tr key={customer.id}>
              {Object.values(customer).map((value, index) => (
                <td key={index}>{value}</td>
              ))}
              <td>
                <button onClick={() => handleEdit(customer.id)}>Edit</button>
                <button onClick={() => handleDelete(customer.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CustomerTable1;
